                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Thông tin BHXH *"
                                            defaultValue={props.data?.bhxh}
                                            error={!!errors["bhxh"]}
                                            helperText={String(
                                                errors["bhxh"] ? errors["bhxh"].message : ""
                                            )}
                                            {...register("bhxh")}
                                        />
                                    </FormControl>
                                </Stack>
                            </FormGroup>
                        </CardContent>
                    </Card>
                    {/* End thông tin hợp đồng */}

                    {/* Thông tin chấm dứt hợp đồng */}
                    <Card sx={{ mb: 1 }}>
                        <CardHeader
                            title="Thông tin chấm dứt hợp đồng"
                            titleTypographyProps={{ variant: "h6" }}
                        ></CardHeader>
                        <CardContent>
                            <FormGroup>
                                <Stack direction="row" spacing={2}>
                                    <FormControl fullWidth>
                                        <LocalizationProvider dateAdapter={AdapterDayjs}>
                                            <DatePicker
                                                label="Ngày thôi việc"
                                                value={defaultValues?.quit_job_date}
                                                onChange={(newValue) => {
                                                    setValue("quit_job_date", newValue)
                                                    handleChange("quit_job_date", newValue)
                                                }}
                                                renderInput={(params) => <TextField {...params} />}
                                            />
                                        </LocalizationProvider>
                                    </FormControl>
                                    <FormControl fullWidth>
                                        <LocalizationProvider dateAdapter={AdapterDayjs}>
                                            <DatePicker
                                                label="Ngày chấm dứt BHXH"
                                                value={defaultValues?.end_bhxh}
                                                onChange={(newValue) => {
                                                    setValue("end_bhxh", newValue)
                                                    handleChange("end_bhxh", newValue)
                                                }}
                                                renderInput={(params) => <TextField {...params} />}
                                            />
                                        </LocalizationProvider>
                                    </FormControl>
                                    <FormControl fullWidth>
                                        <LocalizationProvider dateAdapter={AdapterDayjs}>
                                            <DatePicker
                                                label="Ngày chấm dứt BHYT"
                                                value={defaultValues?.end_bhyt}
                                                onChange={(newValue) => {
                                                    setValue("end_bhyt", newValue)
                                                    handleChange("end_bhyt", newValue)
                                                }}
                                                renderInput={(params) => <TextField {...params} />}
                                            />
                                        </LocalizationProvider>
                                    </FormControl>
                                    <FormControl fullWidth>
                                        <TextField
                                            id="quit_job_number"
                                            type="number"
                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Số quyết định thôi việc"
                                            value={props.data?.quit_job_number}
                                            error={!!errors["quit_job_number"]}
                                            helperText={String(
                                                errors["quit_job_number"]
                                                    ? errors["quit_job_number"].message
                                                    : ""
                                            )}
                                            {...register("quit_job_number")}
                                        />
                                    </FormControl>
                                    <FormControl fullWidth>
                                        <TextField
                                            id="quit_job_reason"
                                            type="text"
                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Lý do thôi việc"
                                            defaultValue={props.data?.quit_job_reason}
                                            error={!!errors["quit_job_reason"]}
                                            helperText={String(
                                                errors["quit_job_reason"]
                                                    ? errors["quit_job_reason"].message
                                                    : ""
                                            )}
                                            {...register("quit_job_reason")}
                                        />
                                    </FormControl>
                                </Stack>
                            </FormGroup>
                        </CardContent>
                    </Card>
                    {/* End thông tin chấm dứt hợp đồng */}

                    {/* Begin thông tin lương */}
                    <Card sx={{ mb: 1 }}>
                        <CardHeader
                            title="Thông tin lương"
                            titleTypographyProps={{ variant: "h6" }}
                        ></CardHeader>
                        <CardContent>
                            <FormGroup>
                                <Stack direction="row" spacing={2}>
                                    <FormControl fullWidth>
                                        <TextField
                                            id="salary"
                                            type="number"
                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Lương cơ bản *"
                                            defaultValue={props.data?.salary}
                                            error={!!errors["salary"]}
                                            helperText={String(
                                                errors["salary"] ? errors["salary"].message : ""
                                            )}
                                            {...register("salary")}
                                        />
                                    </FormControl>
                                    <FormControl fullWidth>
                                        <TextField
                                            id="bank_number"
                                            type="text"
                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Số tài khoản ngân hàng *"
                                            defaultValue={props.data?.bank_number}
                                            error={!!errors["bank_number"]}
                                            helperText={String(
                                                errors["bank_number"] ? errors["bank_number"].message : ""
                                            )}
                                            {...register("bank_number")}
                                        />
                                    </FormControl>
                                    <FormControl fullWidth>
                                        <TextField
                                            id="bank_name"
                                            type="text"
                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Tên ngân hàng *"
                                            defaultValue={props.data?.bank_name}
                                            error={!!errors["bank_name"]}
                                            helperText={String(
                                                errors["bank_name"] ? errors["bank_name"].message : ""
                                            )}
                                            {...register("bank_name")}
                                        />
                                    </FormControl>
                                    <FormControl fullWidth>
                                        <TextField
                                            id="self_reduce"
                                            type="number"
                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Giảm trừ bản thân *"
                                            defaultValue={props.data?.self_reduce}
                                            error={!!errors["self_reduce"]}
                                            helperText={String(
                                                errors["self_reduce"] ? errors["self_reduce"].message : ""
                                            )}
                                            {...register("self_reduce")}
                                        />
                                    </FormControl>
                                    <FormControl fullWidth>
                                        <TextField
                                            id="number_reduce"
                                            type="number"
                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Số người giảm trừ *"
                                            defaultValue={props.data?.number_reduce}
                                            error={!!errors["number_reduce"]}
                                            helperText={String(
                                                errors["number_reduce"] ? errors["number_reduce"].message : ""
                                            )}
                                            {...register("number_reduce")}
                                        />
                                    </FormControl>
                                </Stack>
                                <Stack direction="row" spacing={2} maxWidth={231}>
                                    <FormControl fullWidth>
                                        <TextField
                                            id="dependence_reduce"
                                            type="number"
                                            sx={{ paddingBottom: 2 }}
                                            variant="outlined"
                                            label="Giảm trừ người phụ thuộc *"
                                            defaultValue={props.data?.dependence_reduce}
                                            error={!!errors["dependence_reduce"]}
                                            helperText={String(
                                                errors["dependence_reduce"] ? errors["dependence_reduce"].message : ""
                                            )}
                                            {...register("dependence_reduce")}
                                        />
                                    </FormControl>
                                </Stack>
                            </FormGroup>
                        </CardContent>
                    </Card>
                    {/* End thông tin lương */}
                    {/*Begin thông tin phụ cấp*/}
                    <Card sx={{ mb: 1 }}>
                        <CardHeader
                            title="Thông tin phụ cấp / khấu trừ"
                            titleTypographyProps={{ variant: "h6" }}
                        ></CardHeader>
                        <CardContent>
                            <FormGroup>
                                <Grid container spacing={2}>
                                    <Grid item xs={8}>
                                        <Stack direction="row" spacing={2} maxWidth={725}>
                                            <FormControl fullWidth>
                                                <InputLabel id="select-beneficiares-type">
                                                    Loại phụ cấp
                                                </InputLabel>
                                                <Select
                                                    labelId="select-beneficiares-type"
                                                    id="select-beneficiares-type"
                                                    sx={{ marginBottom: 2 }}
                                                    // value={}
                                                    label="Loại phụ cấp"
                                                    onChange={(event: SelectChangeEvent) => {
                                                        setBeneficType(event.target.value);
                                                    }}
                                                >
                                                    {beneficiariesType.map((item: any, index: number) => (
                                                        <MenuItem key={index} value={item.code}>
                                                            {item.name}
                                                        </MenuItem>
                                                    ))}
                                                </Select>
                                            </FormControl>
                                            <FormControl fullWidth>
                                                <InputBeneficiariesFilter title={"Phụ cấp / khấu trừ"}
                                                    multiple={false}
                                                    onChange={filterBenificiaries} size={"medium"}
                                                    type={beneficType} />
                                            </FormControl>
                                            {!itemUserBeneficiaries.is_percent && (
                                                <FormControl fullWidth>
                                                    <TextField
                                                        id="beneficiaries_value"
                                                        type="number"
                                                        value={itemUserBeneficiaries.value}
                                                        sx={{ paddingBottom: 2 }}
                                                        variant="outlined"
                                                        label="Số tiền"
                                                        name="value"
                                                        onChange={handleChangeUserBeneficiaries}
                                                    />
                                                </FormControl>
                                            )}
                                            {itemUserBeneficiaries.is_percent && (
                                                <FormControl fullWidth>
                                                    <TextField
                                                        id="beneficiaries_percent"
                                                        type="number"
                                                        value={itemUserBeneficiaries.percent}
                                                        sx={{ paddingBottom: 2 }}
                                                        variant="outlined"
                                                        label="Phần trăm"
                                                        name="percent"
                                                        InputProps={{ inputProps: { min: 0, max: 100, step: 0.5, } }}
                                                        onChange={handleChangeUserBeneficiaries}
                                                    />
                                                </FormControl>
                                            )}

                                        </Stack>
                                        <Stack direction="row" spacing={2} mb={2} maxWidth={478}>
                                        <LocalizationProvider dateAdapter={AdapterDayjs}>
                                            <FormControl fullWidth>
                                                <DatePicker
                                                    label="Từ ngày"
                                                    value={itemUserBeneficiaries.from_date }
                                                    inputFormat="DD/MM/YYYY"
                                                    onChange={(newValue) => {
                                                        handleChangeDateBeneficiaries("from_date", newValue)
                                                            setValue("from_date", newValue)
                                                        }}
                                                    renderInput={(params) => <TextField {...params}
                                                    name="from_date" />}
                                                />
                                            </FormControl>
                                            <FormControl fullWidth>
                                                <DatePicker
                                                    label="Đến ngày"
                                                    value={itemUserBeneficiaries.to_date}
                                                    inputFormat="DD/MM/YYYY"
                                                    onChange={(newValue) => {
                                                        handleChangeDateBeneficiaries("to_date", newValue)
                                                            setValue("to_date", newValue)
                                                        }}
                                                    renderInput={(params) => <TextField {...params}
                                                    name="to_date" />}
                                                />
                                            </FormControl>
                                            </LocalizationProvider>
                                        </Stack>
                                    </Grid>
                                    <Grid item xs={4}>
                                        <Button sx={{ mt: "10px" }} type="button" variant="contained"
                                            onClick={() => handleAddBeneficiaries()}>
                                            <AddIcon /> Thêm
                                        </Button>
                                    </Grid>
                                </Grid>

                                <Stack direction="row" spacing={2}>
                                    <WrapperTableComponent
                                        columns={userBenificiariesColumn}
                                        rows={dataUserBeneficiaries as never}
                                        loading={false}
                                        meta={null}
                                        onDelete={handleDeleteBeneficiaries}
                                    />
                                </Stack>
                            </FormGroup>
                        </CardContent>
                    </Card>
                    {/*End thông tin phụ cấp*/}
                    {/* Begin thông tin tiêm vắc xin */}
                    <Card sx={{ mb: 1 }}>
                        <CardHeader
                            title="Thông tin tiêm vắc xin"
                            titleTypographyProps={{ variant: "h6" }}
                        ></CardHeader>
                        <CardContent>
                            <FormGroup>
                                <Grid container spacing={2}>
                                    <Grid item xs={8}>
                                        <Stack direction="row" spacing={2} maxWidth={725}>
                                            <FormControl fullWidth>
                                                <TextField
                                                    id="salary"
                                                    type="text"
                                                    sx={{ paddingBottom: 2 }}
                                                    variant="outlined"
                                                    label="Lần tiêm"
                                                    name="injection_time"
                                                    defaultValue={valuesVacxin.injection_time}
                                                    onChange={handleChangeValueVacxin}
                                                />
                                            </FormControl>
                                            <FormControl fullWidth>
                                                <LocalizationProvider dateAdapter={AdapterDayjs}>
                                                    <DatePicker
                                                        label="Ngày tiêm"
                                                        value={defaultValuesInjectionDate.injection_date || ""}
                                                        inputFormat="DD/MM/YYYY"
                                                        onChange={(newValue) => {
                                                            handleChangeValueInjectionDate("injection_date", newValue)
                                                            setValue("injection_date", newValue)
                                                        }}
                                                        renderInput={(params) => <TextField {...params}
                                                            name="injection_date" />}
                                                    />
                                                </LocalizationProvider>
                                            </FormControl>
                                            <FormControl fullWidth>
                                                <TextField
                                                    id="type_vacxin"
                                                    type="text"
                                                    sx={{ paddingBottom: 2 }}
                                                    variant="outlined"
                                                    label="Loại vắc xin"
                                                    name="type_vacxin"
                                                    defaultValue={valuesVacxin.type_vacxin}
                                                    onChange={handleChangeValueVacxin}
                                                />
                                            </FormControl>
                                        </Stack>
                                    </Grid>
                                    <Grid item xs={4}>
                                        <Button sx={{ mt: "10px" }} type="button" variant="contained"
                                            onClick={() => handleAddVacxin()}>
                                            <AddIcon /> Thêm
                                        </Button>
                                    </Grid>
                                </Grid>


                                <Stack direction="row" spacing={2}>
                                    <WrapperTableComponent
                                        columns={vacxinColumns}
                                        rows={dataVacXin as never}
                                        loading={false}
                                        meta={null}
                                        onDelete={handleDeleteVacxin}
                                    />
                                </Stack>
                            </FormGroup>
                        </CardContent>
                    </Card>
                    {/* End thông tin tiêm vắc xin */}
                </DialogContent>
            </form>
            {/* </DialogContent> */}
            <DialogActions>
                <Button variant="contained" onClick={handleClose} color="error">
                    <CloseIcon /> Đóng
                </Button>
                <Button type="submit" variant="contained" autoFocus form="users-form">
                    <SaveIcon /> Lưu
                </Button>
            </DialogActions>
        </Dialog>
    );
};

export default CreateDialog;
