import React, {useEffect} from 'react';
import {Autocomplete, TextField} from "@mui/material";
import {userTabs} from "../../pages/users/properties/properties";
import _ from "lodash";

const InputEmployeeStatusFilter = (props: any) => {   
    const [workingStatus,setWokingStatus] = React.useState(userTabs)
    const [defaultValue, setDefaultValue] = React.useState(null as any)
    useEffect(() => {
        if(props.data){
            let value = _.find(workingStatus,(item) => {
                return item.code === props.data;
            })
            setDefaultValue(value);
        }
    },[props.data])
    const handleOnChange = (event: any, values: any) => {
        setDefaultValue(values)
        props.onChange(values);
    }
    return (
        <Autocomplete
            sx={{zIndex: 1000}}
            multiple={props.multiple}
            id="combo-box-employee-status"
            size={props.size || "small"}
            onChange={handleOnChange}
            value={defaultValue}
            options={workingStatus}
            getOptionLabel={option => option?.label}
            renderInput={(params) => <TextField {...params} label={"Trạng thái làm việc"} defaultValue={props.data}/>}
        />
    );
};

export default InputEmployeeStatusFilter;
