import {Button, Checkbox, FormControlLabel, Grid, TextField, Typography} from '@mui/material';
import {useNavigate} from 'react-router-dom';
import AlertComponent from '../../components/alert.component';
import ProcessComponent from '../../components/process.component';
import {CreateUserValidation} from './validation/login.validation';
import {useForm} from "react-hook-form";
import {yupResolver} from '@hookform/resolvers/yup';
import {useMutation} from "react-query";
import {login} from "../../ApiService/auth.api";
import {Helmet} from "react-helmet";
import React from "react";
import {GoogleLogin} from "@react-oauth/google";

const LoginComponent = () => {
    let navigate = useNavigate();
    const {register, handleSubmit, formState: {errors}} = useForm({
        resolver: yupResolver(CreateUserValidation)
    });

    const {mutate} = useMutation("login", (info: any) => {
            return login(info);
        },
        {
            onSuccess: (data) => {
                navigate('/users');
            }
        }
    )
    const onSubmitHandler = (info: any) => {
        mutate(info);
    };

    const responseMessage = (response: any) => {
        console.log(response);
    };
    const errorMessage = (error: any) => {
        console.log(error);
    };

    return (
        <>
            <Helmet>
                <script src="https://accounts.google.com/gsi/client" async defer></script>
            </Helmet>
            <AlertComponent/>
            <ProcessComponent/>
            <div className='App login'>
                {/*<div id="g_id_onload"*/}
                {/*     data-client_id="968912013359-k3dlkhtuml6thouv2clttujfnt15ie45.apps.googleusercontent.com"*/}
                {/*     data-context="signin"*/}
                {/*     data-ux_mode="popup"*/}
                {/*     data-callback="handleLogin"*/}
                {/*     data-itp_support="true">*/}
                {/*</div>*/}

                {/*<div className="g_id_signin"*/}
                {/*     data-type="standard"*/}
                {/*     data-shape="rectangular"*/}
                {/*     data-theme="outline"*/}
                {/*     data-text="signin_with"*/}
                {/*     data-size="large"*/}
                {/*     data-logo_alignment="left">*/}
                {/*</div>*/}
                <form className="form" method="post" onSubmit={handleSubmit(onSubmitHandler)}>
                    <Typography variant="h5" component="h2" mb={3} align="center">
                        Đăng Nhập
                    </Typography>
                    <TextField
                        id="email"
                        required
                        sx={{paddingBottom: 2}}
                        variant="outlined"
                        label="Email"
                        {...register('email')}
                        error={!!errors.email}
                        helperText={errors.email && String(errors.email?.message)}
                    />
                    <TextField
                        id="password"
                        required
                        type="password"
                        sx={{paddingBottom: 2}}
                        variant="outlined"
                        label="Mật Khẩu"
                        {...register('password')}
                        error={!!errors.password}
                        helperText={errors.password && String(errors.password?.message)}
                    />
                    <Grid container spacing={4} pt={0.5}>
                        <Grid item xs={6} sx={{display: 'flex', alignContent: 'center', alignItems: 'flex-start'}}>
                            <FormControlLabel label="Remeber me" defaultChecked control={<Checkbox/>}/>
                        </Grid>
                        <Grid item xs={6}>
                            <Typography variant="body1" component="a" mb={3} align="center" href='#'
                                        sx={{
                                            display: 'flex',
                                            flexDirection: 'row-reverse',
                                            justifyContent: 'flext-start',
                                            mt: '0.5rem'
                                        }}>
                                Quên mật khẩu?
                            </Typography>

                        </Grid>
                    </Grid>
                    <Button type="submit" color="primary" variant="contained" sx={{fontWeight: 600}}>
                        ĐĂNG NHẬP
                    </Button>
                    <GoogleLogin onSuccess={responseMessage} onError={errorMessage} />
                </form>
            </div>
        </>
    );
};

export default LoginComponent;
