import React, {useEffect} from 'react';
import {Autocomplete, TextField} from "@mui/material";
import {useQuery} from "react-query";
import {getSalaryType} from "../../ApiService/salaryType.api";
import {getContractType} from "../../ApiService/contractType.api";
import _ from "lodash";

const InputContractTypeFilter = (props: any) => {
    const [contractType, setContractType] = React.useState([] as any)
    const [defaultValue, setDefaultValue] = React.useState(props.multiple ? [] : null as any)
    useEffect(() => {
        if (props.data && contractType.length > 0) {
            let value = _.find(contractType, (item) => {
                return item.id === props.data;
            })
            setDefaultValue(value);
        }
    }, [props.data, contractType])
    // API get ALL Users
    useQuery({
        queryKey: ["getContractType"],
        queryFn: () => getContractType({params:{skip_page: 1}}),
        onSuccess: (data) => {
            setContractType(data.data);
        }
    })
    const handleOnChange = (event: any, values: any) => {
        setDefaultValue(values)
        props.onChange(values);
    }
    return (
        <Autocomplete
            multiple={props.multiple}
            id="combo-box-contract-type"
            options={contractType}
            getOptionLabel={(option) => option.name}
            onChange={handleOnChange}
            value={defaultValue}
            size={props.size || "small"}
            renderInput={(params) => <TextField {...params} label={props.title} />}
        />
    );
};

export default InputContractTypeFilter;
