import React, {useEffect} from 'react';
import {Autocomplete, TextField} from "@mui/material";
import _ from "lodash";

const InputGenderFilter = (props: any) => {
    const [gender, setGender] = React.useState([
        {
            "name": "Nam",
            "code": "male"
        },
        {
            "name": "Nữ",
            "code": "female"
        }
    ]);
    const [defaultValue, setDefaultValue] = React.useState(null as any)
    useEffect(() => {
        if(props.data){
            let value = _.find(gender,(item) => {
                return item.name === props.data;
            })
            setDefaultValue(value);
        }
    },[props.data])
    const handleOnChange = (event: any, values: any) => {
        setDefaultValue(values);
        props.onChange(values);
    }
    return (
        <Autocomplete
            multiple={props.multiple}
            id="combo-box-salary-tyupe"
            size={props.size || "small"}
            options={gender}
            getOptionLabel={(option) => option.name}
            value={defaultValue}
            onChange={handleOnChange}
            renderInput={(params) => <TextField {...params} label={props.title} defaultValue={props.data} />}
        />
    );
};

export default InputGenderFilter;
