import React, {useEffect} from 'react';
import {Autocomplete, TextField} from "@mui/material";
import {useQuery} from "react-query";
import {getDesignations} from "../../ApiService/designations.api";
import _ from "lodash";

const InputDesignationFilter = (props: any) => {
    const [designation, setDesignation] = React.useState([] as any)
    const [defaultValue, setDefaultValue] = React.useState(props?.multiple ? [] : null as any)
    useEffect(() => {
        recheckData();
    },[props.data,designation])
    const recheckData = () => {
        if(props.data && designation.length > 0){
            let value = _.find(designation,(item) => {
                return item.id === props.data;
            })
            console.log(1);
            setDefaultValue(value);
        }
    }
    //API get all department
    useQuery({
        queryKey: ["getAllDesignation"],
        queryFn: () => getDesignations({params: {skip_page: 1}}),
        onSuccess: (data) => {
            setDesignation(data.data);
        }
    });

    const handleOnChange = (event: any, values: any) => {
        setDefaultValue(values);
        props.onChange(values);
    }
    return (
        <Autocomplete
            multiple={props.multiple}
            id="combo-box-designation"
            options={designation}
            getOptionLabel={(option) => option.name}
            value={defaultValue}
            onChange={handleOnChange}
            size={props.size || "small"}
            renderInput={(params) => <TextField {...params} label={props.title}/>}
        />
    );
};

export default InputDesignationFilter;
