import React, {useEffect} from 'react';
import {Autocomplete, TextField} from "@mui/material";
import {useQuery} from "react-query";
import _ from "lodash";
import {getLaborSupplier} from "../../ApiService/laborSupplier.api";

const InputLaborFilter = (props: any) => {
    const [laborSupplier, setLaborSupplier] = React.useState([] as any)
    const [defaultValue, setDefaultValue] = React.useState(props.multiple ? [] : null as any)
    useEffect(() => {
        if (props.data && laborSupplier.length > 0) {
            let value = _.find(laborSupplier, (item) => {
                return item.id === props.data;
            })
            setDefaultValue(value);
        }
    }, [props.data, laborSupplier])
    // API get ALL Users
    useQuery({
        queryKey: ["getLaborSupplier"],
        queryFn: () => getLaborSupplier({params: {skip_page: 1}}),
        onSuccess: (data) => {
            setLaborSupplier(data.data);
        }
    })
    const handleOnChange = (event: any, values: any) => {
        console.log(values);
        setDefaultValue(values);
        props.onChange(values);
    }
    return (
        <Autocomplete
            multiple={props.multiple}
            id="combo-box-salary-type"
            options={laborSupplier}
            getOptionLabel={(option) => option.name}
            onChange={handleOnChange}
            value={defaultValue}
            size={props.size || "small"}
            renderInput={(params) => <TextField {...params} label={props.title}/>}
        />
    );
};

export default InputLaborFilter;
