import React, {useEffect} from 'react';
import {Autocomplete, TextField} from "@mui/material";
import {useQuery} from "react-query";
import {getWorkingShift} from "../../ApiService/workShift.api";
import _ from "lodash";

const InputWorkShiftFilter = (props: any) => {
    const [workShift, setWorkShift] = React.useState([] as any)
    const [defaultValue, setDefaultValue] = React.useState(props.multiple ? [] : null as any)
    useEffect(() => {
        if (props.data && workShift.length > 0) {
            let value = _.find(workShift, (item) => {
                return item.id === props.data;
            })
            setDefaultValue(value);
        }
    }, [props.data, workShift])
    // API get ALL Users
    useQuery({
        queryKey: ["getWorkingShift"],
        queryFn: () => getWorkingShift({params:{skip_page: 1}}),
        onSuccess: (data) => {
            setWorkShift(data.data);
        }
    })
    const handleOnChange = (event: any, values: any) => {
        setDefaultValue(values);
        props.onChange(values);
    }
    return (
        <Autocomplete
            multiple={props.multiple}
            id="combo-box-work-shift"
            options={workShift}
            getOptionLabel={(option) => option.name}
            onChange={handleOnChange}
            value={defaultValue}
            size={props.size || "small"}
            renderInput={(params) => <TextField {...params} label={props.title} />}
        />
    );
};

export default InputWorkShiftFilter;
